const html=document.querySelector("html"),burger=document.querySelector("#burger"),menu=document.querySelector(".menu");function isApple(){return["iPad Simulator","iPhone Simulator","iPod Simulator","iPad","iPhone","iPod"].includes(navigator.platform)||navigator.userAgent.includes("Mac")&&"ontouchend"in document}function resizeHeader(){const e=document.querySelector(".header");window.scrollY>30?e.classList.add("small"):e.classList.remove("small")}function openMenu(){burger.classList.toggle("open"),menu.classList.toggle("open"),html.classList.toggle("scrollOff")}burger.addEventListener("click",openMenu),window.addEventListener("load",()=>{isApple()&&document.html.classList.add("ios")}),document.addEventListener("scroll",()=>{resizeHeader()});